settings.cardCss:
.card,
.card * {
    box-sizing: border-box;
}    
.card {
    position: relative;
    width: 100%;
    height: 100%;
}
.card .content {
    height: 100%;
}
.card .upper-bar {
    position: relative;
    z-index: 0;
    overflow: hidden;
    height: 70%;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,.26);
    color: #fff;
}
.card .lower-bar {
    height: 30%;
    padding-left: 16px;
    display: flex;
    flex-direction: row;
    align-items: center;
}

.card .right-top-bar {
    display: none;
    position: absolute;
    top: 10px;
    right: 10px;
    display: flex;
    flex-direction: row;
    align-items: center;
}

.card .right-top-bar .icon-container {
    display: none;
    box-shadow: 0px 0px 4px 2px rgba(0,0,0,.26);
    cursor: pointer;
    border: 3px solid #fff;
    color: #fff;
    background-color: rgb(255, 157, 12);
}

.card .right-top-bar .icon-container .icon {
    color: #fff;
}

.card .right-top-bar .icon-container:hover {
    border: 3px solid rgb(255, 157, 12);
    color: rgb(255, 157, 12);
    background-color: #fff;
}

.card .right-top-bar .icon-container:hover .icon {
    color: rgb(255, 157, 12);
}

.card .image-shade {
    width: 100%;
    height: 100%;
    padding-left: 16px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    background-color: rgba(255, 157, 12, .5);
    z-index: 0;
}
.card h1 {
    margin: 0;
    font-size: 20px;
    font-weight: bold;
    line-height: 30px;
}
.card .icon-container {
    height: 40px;
    width: 40px;
    border-radius: 50%;
    border: 3px solid rgb(255, 157, 12);
    text-align: center;
}
.card .description {
    margin-left: 16px;
    font-size: 20px;
    font-weight: 500;
    color: rgb(255, 157, 12);
}
.card .value {
    font-size: 40px;
    font-weight: 300;
    letter-spacing: -2px;
}
.card .icon {
    line-height: 36px;
    color: rgb(255, 157, 12);
}
.card video {
    position: absolute;
    top: 75%;
    left: 0;
    width: 160%;
    z-index: -1;
    transform: translateY(-70%);
}

