actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));

openDeleteEntityDialog();

function openDeleteEntityDialog() {
    let title = '"' +  entityName + '"' + " Sil";
    let content = '"' +
        entityName + '" silmek istediÄinize emin misiniz?';
    dialogs.confirm(title, content, 'Ä°PTAL', 'SÄ°L').subscribe(
        function(result) {
            if (result) {
                deleteEntity();
            }
        }
    );
}

function deleteEntity() {
    deviceService.deleteDevice(entityId.id).subscribe(
        function success() {
            widgetContext.updateAliases();
        },
        function fail() {
            showErrorDialog();
        }
    );
}

function showErrorDialog() {
    let title = 'Error';
    let content = 'An error occurred while deleting the sensor. Please try again.';
    dialogs.alert(title, content, 'CLOSE').subscribe(
        function(result) {}
    );
}

